
public class BreakTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		for(int i = 0; i < 1; i++) {
			for(int j = 0; j < 10; j++) {
				if(j == 1) break;
			}
			
			System.out.println("안쪽 for 빠져나옴");
		}System.out.println("main종료");
		
		
		
		
				
		
		
		
		
		
		
		
		
		
		
		
	}

}
