
public class TestWhile2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		
		//구구단 2-9단 while문
		
		int i = 2;
		int j = 1;
		
		while(i <= 9) {
			
			j = 1;
			while(j <= 9) {
				System.out.println(i + "x" + j);
				j++;
				
			}i++;
			System.out.println();
			
		}
		
		
	}

}
